import unittest
from flask import Flask
from Database_lib import db, User, init_app

class TestDatabase(unittest.TestCase):
    def setUp(self):
        self.app = Flask(__name__)
        self.app.config['SQLALCHEMY_DATABASE_URI'] = 'postgresql://postgres:sfdc@localhost:5432/Microservice'
        self.app.config['SQLALCHEMY_TRACK_MODIFICATIONS'] = False
        init_app(self.app)
        self.app.app_context().push()

    def test_create_user(self):
        user = User(email='test@example.com', password='password', name='Test', gender='M')
        db.session.add(user)
        db.session.commit()
        self.assertEqual(User.query.count(), 1)

if __name__ == '__main__':
    unittest.main()
